    public static double sin(int deg) {
        return sinTable[deg % 360];
    }

    public static double cos(int deg) {
        return cosTable[deg % 360];
    }

    public static double tan(int deg) {
        return tanTable[deg % 360];
    }

    public static double asin(double val) {
        double deg = 0;
        double closest = 9999;

        for(int i = 0; i < sinTable.length; i++) {
            double v = sinTable[i];
            double distance = Math.abs(v - val);

            if(distance < closest) {
                deg = i;
                closest = distance;
            }
        }

        return deg;
    }

    public static double acos(double val) {
        double deg = 0;
        double closest = 9999;

        for(int i = 0; i < cosTable.length; i++) {
            double v = cosTable[i];
            double distance = Math.abs(v - val);

            if(distance < closest) {
                deg = i;
                closest = distance;
            }
        }

        return deg;
    }

    public static double atan(double val) {
        double deg = 0;
        double closest = 9999;

        for(int i = 0; i < tanTable.length; i++) {
            double v = tanTable[i];
            double distance = Math.abs(v - val);

            if(distance < closest) {
                deg = i;
                closest = distance;
            }
        }

        return deg;
    }
